class r{width;height;fogRadius;visited;constructor(i,t,s=5){this.width=i,this.height=t,this.fogRadius=s,this.visited=new Set}markVisited(i,t){if(this.isInBounds(i,t)){const s=this.toFlatIndex(i,t);this.visited.add(s)}}isVisited(i,t){if(!this.isInBounds(i,t))return!1;const s=this.toFlatIndex(i,t);return this.visited.has(s)}isVisible(i,t,s,e){return Math.sqrt((i-s)**2+(t-e)**2)<=this.fogRadius}getVisibleTiles(i,t){const s=new Set,e=this.fogRadius*this.fogRadius;for(let d=0;d<this.height;d++)for(let n=0;n<this.width;n++)if((n-i)**2+(d-t)**2<=e){const h=this.toFlatIndex(n,d);s.add(h)}return s}updatePlayerPosition(i,t){const s=this.getVisibleTiles(i,t);for(const e of s)this.visited.add(e)}reset(){this.visited.clear()}isInBounds(i,t){return i>=0&&i<this.width&&t>=0&&t<this.height}toFlatIndex(i,t){return t*this.width+i}}export{r as ExplorationTracker};
